INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (1, '出走的泰奥', 1, 37, 4, '彼特·赫尔特林', 'img', 6, '二十一世纪出版社', 78000, 73.00, 127, 8.80, '9787539137827', '0', '0', NULL, '《出走的泰奥》内容简介：泰奥因为受不了家里吵吵闹闹的气氛，出走了……他在汽车的肚子底下和树林中吓人的小木房里过夜，认识了慈爱的咹咹老爸、好心的货车司机，也遭遇了犯罪团伙……最后终于懂得了葡萄酒并不全是甜的，生活尤其是这样的道理。\n家里吵吵闹闹的气氛真让泰奥受不了，虽然他的老师佩尔希夫人能理解他，有心事他也能向天花板上的小人儿倾诉；可是，他还是决定离开这里所有的一切。他把书包往教室外的大柜子里一塞，出走了……\n他在汽车的肚子底下和树林中吓人的小木屋里过夜，他认识了慈爱的经营欢乐转盘的咹咹老爸、好心的货车司机凯玛尔，也被一个倒卖汽车的犯罪团伙幽禁在一个黑暗的阁楼上……所幸的是，这一切都如恶梦一般过去了，咹咹老爸的故事和他说的一句话却久久地留在了泰奥的心里——葡萄酒并不全是甜的，生活尤其是这样。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-9-17 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (2, '我的小小忧伤-这不公平！', 1, 37, 1, '爱格雷贝特', 'img', 2, '中国少年儿童出版社', 500, 57.00, 32, 9.00, '20855238-1', '0', '0', NULL, '在学校被同学嘲笑的日子真不好过，太可气了！这是孩子一个不能对家长说的小秘密！', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-9-17 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (3, '希利尔讲世界地理', 1, 6, 6, '（美）希利尔著', 'img', 8, '贵州教育', 240000, 95.00, 277, 36.80, '9787545600865', '0', '0', NULL, '孩子们应该懂得，世界总是在不断变化着的，最新一版并不意味着最后一版，“世界地图”不也是如此吗？即便这样，这本书仍不失为一本绝佳的地理启蒙读物，我把它读给九岁的儿子听，我们两个人都很喜欢。他经常在地图上找来找去，趴在我的肩头读啊读，还把他从书中学到的东西讲给别人听。对他而言，“地理”显然已经不再是个干巴巴、硬邦邦的字眼儿，而是听上去跟“历险”更沾边儿些。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2016-1-10 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (4, '学会爱自己-不要随便摸我', 1, 37, 1, '[美]珊蒂·克雷文著[美]茱蒂·柏斯玛绘', 'img', 3, '青岛出版社', 1200, 91.00, 24, 12.00, '21000723-1', '0', '0', NULL, '《不要随便摸我》是教给孩子进行自我性保护的图书。书中通过一个儿童性侵犯的案例，教给孩子自己身体的哪些隐秘部位是任何人都不能随意触摸的，以及遇到性侵犯这样的事情时该如何处理。这本书曾获得本杰明?富兰克林亲子教养类奖，是美国很多家庭和学校对孩子进行性保护教育的教材。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-9-18 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (5, '学会爱自己-不要随便亲我', 1, 37, 1, '克雷文', 'img', 3, '青岛出版社', 1200, 91.00, 24, 12.00, '21000723-2', '0', '0', NULL, '《不要随便亲我》这本书不但教给孩子要勇敢地表达自己的观点，很好地保护自己，也告诉大人们一个道理：以孩子能接受的方式表达对他（她）的爱，才是真正爱他（她）！', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (6, '学会爱自己-不要随便跟陌生人走', 1, 37, 1, '克雷文', 'img', 3, '青岛出版社', 1200, 96.00, 24, 12.00, '21000723-3', '0', '0', NULL, '《不要随便跟陌生人走》说的虽然是大家常常挂在嘴边的主题，但是，其中介绍的一些应对危机的方法却是孩子们真正需要的，可以给孩子们很多帮助。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (7, '淘气狗克劳德历险记-度假历险记', 1, 37, 2, '〔英〕亚历克斯T史密斯？著？孙葭？译', 'img', 6, '学习出版社', 6000, 100.00, 93, 16.00, '22850736-2', '0', '0', NULL, '克劳德是只不平凡的小狗，他的生话真的不同寻常！每当亮皮鞋夫妇上班后，克劳德就会忙着去各处历险。这天，克劳德确和跳跳袜决定到海滨度假。他们高高兴兴地盖着沙堡，吃着冰淇琳，晒着日光俗，一切都那么平静又美好。直到他们遇到了海盗船长……\n克劳德和跳跳袜的度假历险开始啦……他们这次又经历了什么呢？', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (8, '淘气狗克劳德历险记-马戏团历险记', 1, 37, 2, '〔英〕亚历克斯T史密斯？著？孙葭？译', 'img', 6, '学习出版社', 6000, 100.00, 93, 16.00, '22850736-3', '0', '0', NULL, '　克劳德是只不平凡的小狗，他的生话真的不同寻常！每当亮皮鞋夫妇上班后，克劳德就会忙着去各处历险。这天，他本来想在公园悠闲地散散步，却被一些慢跑者打扰得一团糟，转头又鬼使神差地加入了马戏团。克劳德的本事真是不得了，既能走钢丝，又能把蛋奶派扔得提溜转，成了马戏团的大明星。这一切事情都很有趣，但克劳德还是必须在亮皮鞋夫妇下班前乖乖地赶回家。幸亏，这儿附近就有一种最快捷的交通方式……你们猜猜是什么呢？', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (9, '我的自然科学课-如何种蔬菜', 1, 7, 2, '【日】三枝博幸 等著', 'img', 6, '天津人民美术出版社', 5000, 100.00, 47, 15.00, '23055769-4', '0', '0', NULL, '这本书中所介绍的蔬菜全部是我们日常所吃的，都非常熟悉，但是它们是怎么长大成熟的？估计很多人却很陌生。\n　　每种蔬菜，都有不同的特性，有的蔬菜喜欢温度高、日照好的环境，有的在凉爽的环境中才能长得好。这种特性的不同是因为它的家乡（原产地）不同而造成的。所以，当你种植蔬菜时，就要了解蔬菜的特性，并根据它的特性来决定栽种的时机，选择培育的方法。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (10, '阅读123系列《虫来没看过》', 1, 7, 2, '张东君，胡妙芬，杨维晟', 'img', 6, '贵州人民出版社', 6000, 80.00, 89, 13.40, '23234608-2', '0', '0', NULL, '看过昆虫吗？观察过昆虫吗？喜欢昆虫吗？害怕昆虫吗？\n　　大家对这种小东西各有想象，这里说的可不是常见的蚂蚁和蟑螂，\n　　而是你从来没有看过的奇特虫虫。\n　　它们奇形怪状、颜色鲜艳的齐聚一堂，是为争夺“金昆虫奖”。让我们听听评审蓝鹊和八色鸟是如何评定其高下的吧。\n　　看完这本书，你会惊讶地发现，\n　　昆虫们看似无意义的行为和样貌，其实都是大有玄机的。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (11, '阅读123系列《叶限》', 1, 37, 2, '谢武彰', 'img', 6, '贵州人民出版社', 6000, 60.00, 89, 13.40, '23234609-2', '0', '0', NULL, '坏心的继母把叶限养在池塘里的大鱼煮来吃了，叶限非常难过。这时，从天而降的老人告诉叶限，继母把鱼骨头埋在粪土里了，这鱼骨头有魔力，只要向他许愿，愿望就能成真。叶限向鱼骨头求来了美丽的羽毛衣和金缕鞋，去参加洞节舞会，回家时不小心掉了一只鞋……这只金缕鞋会被谁捡走？叶限能不能逃离继母的虐待？\n　　鱼骨头又帮谁完成了哪些愿望呢？', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (12, '和英童书永远的杨唤-小纸船', 1, 37, 1, '杨唤', 'img', 2, '河北少儿出版社', 730, 100.00, 28, 19.90, '23292260-6', '0', '0', NULL, '一个善良的年轻灵魂，用一颗天真的诗心来为孩子们歌唱，还有谁比杨唤\n更适合的？朴实的形式，美丽的形象，再加上深刻优美的内容，还有谁的\n童话诗可以媲美杨唤的？在抒情诗里，他的作品有忧郁哀愁的；他在童话\n诗里，却大量地把质朴的、向上的精神，倾注给无邪的幼小者。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (13, '大侦探包肚因系列之《午夜幽灵》', 1, 17, 2, '[德]沃夫冈艾克 著 [德]乌特克劳瑟 绘', 'img', 4, '河北少儿出版社', 3800, 40.00, 59, 12.00, '9787537668651-5', '0', '0', NULL, '《午夜幽灵》讲述的是，布罗莫先生被奇怪的恐吓电话吓破了胆，急忙找到大侦探寻求保护和帮助。当天晚上，布罗莫又接到了一封恐吓信，信中写着“今天晚上12点03分”。包肚因赶到了布罗莫的家中，陪着被吓得神经兮兮的布罗莫先生度过了一个紧张的夜晚……第二天，当包肚因回到家时，一位神秘的气质先生出现了。这个人声称布罗莫先生已经落入了他们的手中……', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (14, '大侦探包肚因系列之《不寻常的小偷》', 1, 17, 2, '[德]沃夫冈艾克 著 [德]乌特克劳瑟 绘', 'img', 4, '河北少儿出版社', 3800, 60.00, 58, 12.00, '9787537668651-2', '0', '0', NULL, '《不寻常的小偷》讲述的是，布劳瑟男爵的家中最近频频出现失窃案，被盗的全是一些金银制的小物品。奇怪的是，连男爵家祖传的黄铜痰盂也被偷走了！布劳瑟男爵请来了大侦探包肚因，希望能尽快找到窃贼。经过侦查，包肚因排除了男爵家中有内贼的可能性。与此同时，房间中的一些蛛丝马迹引起了大侦探的兴趣，这让他的注意力转移到了屋外街角处的卖艺人身上……', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (15, '大侦探包肚因系列之《八音盒的秘密》', 1, 17, 2, '[德]沃夫冈艾克 著 [德]乌特克劳瑟 绘', 'img', 4, '河北少儿出版社', 3800, 66.00, 51, 12.00, '9787537668651-1', '0', '0', NULL, '《八音盒的秘密》讲述的是，一位自称福克斯的美国人想雇佣包肚因，在城里面寻找一个八音盒。线索照片中的日本八音盒并不值钱，而这个美国人不仅肯花大价钱请名侦探寻找，还拒绝透露八音盒的秘密……这引起了大侦探的怀疑。包肚因在走街串巷寻找八音盒的同时，还慢慢摸清楚了福克斯先生的秘密……', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (16, '大侦探包肚因系列之《酒店历险记》', 1, 17, 2, '[德]沃夫冈艾克 著 [德]乌特克劳瑟 绘', 'img', 4, '河北少儿出版社', 3800, 100.00, 56, 12.00, '9787537668651-3', '0', '0', NULL, '《酒店历险记》讲述的是，一位印度贵族即将入住大使酒店。酒店总经理科尼克斯先生为了万无一失，请来了鼎鼎大名的包肚因担任酒店的专职侦探。这可真是个美差！尤其是饭店提供的免费大餐，更是让包肚因不能拒绝这次工作。当然，天下没有免费的午餐——自从印度贵族入住的那一天开始，酒店里就发生了各种各样奇怪的失窃案……', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (17, '大侦探包肚因系列之《幽灵克星》', 1, 17, 2, '[德]沃夫冈艾克 著 [德]乌特克劳瑟 绘', 'img', 4, '河北少儿出版社', 3800, 100.00, 47, 12.00, '9787537668651-6', '0', '0', NULL, '《幽灵克星》讲述的是，马苏卡夫妇是城里一幢古宅的房东。最近一段时间，他们居住的古宅总在“闹鬼”。一到半夜，阁楼上就会传出吓人的怪声音。夫妇二人怀疑是同住在古宅里的花匠在诅咒他们，但是他们又不敢去阁楼亲自察看。于是，他们找到了大名鼎鼎的包肚因，请他去深夜“捉鬼”。包肚因还从来没抓过鬼呢！他做好了万全准备，和自己的小狗刷子一起赶往古宅，一探究竟……', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (18, '大侦探包肚因系列之《青铜大盗》', 1, 17, 2, '[德]沃夫冈艾克 著 [德]乌特克劳瑟 绘', 'img', 4, '河北少儿出版社', 3800, 50.00, 54, 12.00, '9787537668651-4', '0', '0', NULL, '《青铜大盗》讲述的是，一件珍贵的青铜器文物即将从挪威运到城里的博物馆中展出。博物馆馆长金谷伯先生非常在意青铜器的安全，特地请大侦探包肚因与自己的助手克雷伯一同前往奥斯陆，带回青铜器。不料在包肚因出发前，两个不速之客竟找上了他的家门，把他绑架了！等大侦探醒来的时候，发现自己已经被锁在了一个林中小屋里，旁边还有个叫“马克斯”的大块头看着他。包肚因该如何逃生？幕后的主谋又是谁呢？', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (19, '孙悟空在我们村里', 1, 29, 4, '郭风/著', 'img', 9, '湖北少年儿童出版社', 192000, 91.00, 293, 14.80, '9787535334671', '0', '0', NULL, '本书是《百年百部中国儿童文学经典书系》之一，主要包括：孙悟空在我们村里、孙悟空和我的爷爷、抽屉里的晚上、天使的农场、虹的滑梯等作品', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2016-3-8 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (20, '儿童文学·故事第一辑之 额头上的包', 1, 15, 2, '谢华良', 'img', 6, '中国少年儿童出版社', 8000, 100.00, 79, 10.00, '9770257656091-4', '0', '0', NULL, '《儿童文学？故事》是一本面向小学1-3年级（6-19岁）孩子的较为低龄的刊物，以生动活泼的故事为核心，寓教于乐，培养孩子的文学鉴赏能力和写作能力；以提升孩子的文学素养并激发他们对文学的爱好为宗旨，精选优秀中外故事的同时大力鼓励原创故事，努力为中国儿童提供最适合他们的读物。儿童文学出版中心将立志把《儿童文学？故事》打造成为全国小学生低年级读物中的第一品牌。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (21, '安房直子幻想小说代表作2 风与树的歌', 1, 19, 5, '[日]安房直子著', 'img', 7, '少年儿童出版社', 110000, 100.00, 155, 17.00, '978532482429', '0', '0', NULL, '本书是安房直子童话集之一，包括安房直子代表性的《狐狸的窗户》《花椒娃娃》《夕阳之国》等作品。这些作品为我们描绘了一个奇幻的国度，一个精灵出没的世界，这样的一个世界让人着迷，让人久久难忘。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (22, '安房直子幻想小说代表作1 花香小镇', 1, 19, 5, '[日]安房直子著', 'img', 7, '少年儿童出版社', 103000, 100.00, 145, 17.00, '978532482436', '0', '0', NULL, '本书为“安房直子幻想小说代表作”中的一本，包括《小鸟和玫瑰》《黄围巾》《花香小镇》等七部短篇幻想小说作品。安房直子的作品营造了一个个如梦如幻、精美之极的童话世界，让人在单纯透明的色泽中感受生命多重的颜色和意蕴。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (23, '安房直子幻想小说代表作6 黄昏海的故事', 1, 19, 5, '[日]安房直子著', 'img', 8, '少年儿童出版社', 131000, 100.00, 186, 17.00, '978532482443', '0', '0', NULL, '这本书收录了安房直子创作的14篇幻想小说，包括：《黄昏海的故事》、《声音的森林》、《萤火虫》等，堪称是“凄美而魅幻、伤感而温馨的幻想小说经典、宛若现代版格林童话”\n如果你还没有准备好，去爱，去给予，就不要轻易地承诺。海枯石烂，不是随便唱唱的歌，而是将回响在你生命中每个黄昏里的潮汐声，你记得也好，最好你忘掉，一个人得到真爱的机会，就只有这样一次而已…', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (24, '安房直子幻想小说代表作5 银孔雀', 1, 19, 5, '[日]安房直子著', 'img', 8, '少年儿童出版社', 125000, 100.00, 177, 17.00, '9785324982405', '0', '0', NULL, '银色的孔雀，古老的传说，总有些人要为那编织美丽旗帜的使命付出自己的一生。也许所有打动人心的艺术作品的背后，都隐藏着奋斗者美丽的梦想与执着的追求。日本著名儿童文学作家安房直子的幻想童话集《银孔雀》，收集了数篇精美的童话，从中你会品味许多人生哲理，获得许多美的享受。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (25, '哈利波特与魔法石', 1, 19, 4, 'J.K.罗琳', 'img', 10, '人民文学出版社', 228000, 94.00, 191, 19.50, '9787020033430', '0', '0', NULL, '一岁的哈利波特失去父母后，神秘地出现在姨父姨妈家的门前。哈利在姨父家饱受欺凌，度过十年极其痛苦的日子。姨父和姨妈好似凶神恶煞，他们那混世魔王儿子达力一个肥胖、娇惯、欺负人的大块头，更是经济对哈利拳脚相加。哈利的“房间”是位于楼梯口的一个又暗又小的碗橱。十年来，从来没有人为他过过生日。\n但是在他十一岁生日那天，一切都发生了变化，信使猫头鹰带来了一封神秘的信：邀请哈利去一个他以及所有读到哈利故事的人会觉得望远难忘的、不可思议的地方霍格沃茨魔法学校。\n在魔法学校，哈利不仅找着了朋友，学会了空中飞行，骑着一把型号为光轮2000的飞天扫帚打魁地奇球，还得到了一件隐形衣。他发现那里的一切从上课到吃饭到睡觉都充满了魔力，但是一块魔石出现，它与哈利的命运息息相关……', NULL, 1, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (26, '哈利波特与密室', 1, 19, 4, 'J.K.罗琳', 'img', 10, '人民文学出版社', 243000, 97.00, 203, 22.00, '9787020033447', '0', '0', NULL, '哈利·波特在霍格沃茨魔法学校学习一年之后，暑假开始了。他在姨父姨妈家熬过痛苦的假期。正当他准备打点行装去学校时，小精灵多比前来发出警告：如果哈利返回霍格沃茨，灾难将会临头。\n但哈利义无返顾地回到了霍格沃茨，新来的吉罗德·洛哈特教授装腔作势，让他作呕；游荡在女生舆洗室里的幽灵“哭泣的桃金娘”搅得他不得安宁；低年级的小女生金妮对他投来关切目光常令他尴尬不已；小男生科林·克里维“追星”式的跟踪又经常使他落荒而逃。\n但是，这一切仅仅是灾难的序曲。正如多比所预言的，哈利遭受了重重磨难，经历了种种危险，难解之谜又使他煞费苦心：霍格沃茨的学生接二连三地变成了石头。这一切是品德败坏的学生德拉科·马尔福精心策划的杰作？还是忠厚善良的海格无心铸成的大错？或者另有人将在霍格沃茨制造更大的阴谋？这一切又是否与传说中的密室有关？哈利决心揭开谜底……', NULL, 1, 0, NULL, '2015-8-24 00:00:00', NULL, '2016-7-12 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (27, '哈利波特与火焰杯', 1, 19, 5, 'J.K.罗琳', 'img', 10, '人民文学出版社', 515000, 97.00, 433, 56.00, '9787020034635', '0', '0', NULL, '哈利·波特在霍格沃茨魔法学校经过三年的学习和磨炼，逐渐成长为一个出色的巫师。新学年开始前，哈利和好朋友罗恩，赫敏一起去观看精彩的魁地奇世界杯赛，无意间发现了消失十三年的黑魔标记。哈利的心头笼上了一团浓重的阴云，但三个少年依然拥有他们自己的伊甸园。然而，少男少女的心思是那样难以捉摸，三人之间的美好友情竟是那样一波三折，忽晴忽雨……哈利渴望与美丽的秋·张共同走进一个美丽的故事，但这个朦朦胧胧的憧憬却遭受了小小的失意。他要做一个普普通通的四年级魔法学生，可不幸的是，哈利注定永远都不可能平平常常——即使拿魔法界的标准来衡量。黑魔的阴影始终挥之不去，种种暗藏杀机的神秘事件将哈利一步步推向了伏地魔的魔爪。哈利渴望在百年不遇的三强争霸赛中战胜自我，完成三个惊险艰巨的魔法项目，谁知整个竞赛竟是一个天大的黑魔法阴谋。……', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2016-3-5 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (28, '哈利波特与混血王子', 1, 19, 4, 'J.K.罗琳', 'img', 10, '人民文学出版社', 500000, 91.00, 496, 58.00, '9787020053230', '0', '0', NULL, '仲夏的一个夜晚，反常的浓雾笼罩在窗户玻璃上，哈利·波特在女贞路4号德思礼家自己的卧室里紧张地等待着邓布利多教授的来访。\n哈利不太确定邓布利多是否真的会来德思礼家。邓布利多为什么现在要来看他呢？几个星期之后，他就要返校，邓布利多为什么不能等一等呢？哈利六年级的学习似乎就这样出人意料地提前开始了……\n而更加出人意料的事情还在接踵而至：邓布利多终于让斯内普教授如愿以偿，任命其担任黑魔法防御术课教师……哈利从教室的储藏柜里翻到一本魔药课本，它的前任主人是“混血王子”，从此哈利在神秘“王子”的帮助下成为“魔药奇才”……邓布利多开始了给哈利的单独授课，但奇怪的是，邓布利多却经常离开学校外出……在邓布利多的课上，哈利经历了几段关于少年伏地魔的惊心动魄的记忆，揭示了伏地魔不同寻常的身世之谜……\n哈利隐隐觉得这一学期期内普教授和马尔福的关系发生了微妙变化，其中似乎别有一番隐情，而马尔福更是行踪诡秘……哈利试图揭穿马尔福的阴谋，但始终没有成功，直到马尔福把食死徒引进学校，斯内普对邓布利多校长举起了魔杖……\n哈利·波特在魔法世界的历险高潮再次掀起……', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2016-3-5 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (29, '哈利波特与死亡圣器', 1, 19, 5, 'J.K.罗琳', 'img', 10, '人民文学出版社', 516000, 93.00, 574, 66.00, '9787020063659', '0', '0', NULL, '还有四天，哈利就要迎来自己十七岁的生日，成为一名真正的魔法师。然而，他不得不提前离开女贞路4号，永远离开这个他曾经生活过十六年的地方。\n凤凰社的成员精心谋划了秘密转移哈利的计划，以防哈利遭到伏地魔及其追随者食死徒的袭击。然而，可怕的意外还是发生了……\n与此同时，卷土重来的伏地魔已经染指霍格沃茨魔法学校，占领了魔法部，控制了半个魔法界，形势急转直下……\n哈利在罗恩、赫敏的陪伴下，不得不逃亡在外，隐形遁迹。为了完成校长邓布利多的遗命，一直在暗中寻机销毁伏地魔魂器的哈利，意外地获悉如果他们能够拥有传说中的三件死亡圣器，伏地魔将必死无疑。但是，伏地魔也早已开始了寻找死亡圣器的行动，并派出众多食死徒，布下天罗地网追捕哈利……\n哈利与伏地魔在魔法学校的禁林中遭遇到了，哈利倒在伏地魔抢先到手的一件致命的圣器之下……\n然而，伏地魔未能如愿以偿，死亡圣器不可能战胜纯正的灵魂。哈利赢得了这场殊死较量的最终胜利……', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2016-3-5 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (30, '鲁滨孙漂流记（又名：鲁宾逊漂流记）', 1, 23, 6, '丹尼尔·笛福', 'img', 8, '人民文学出版社', 229000, 95.00, 239, 15.00, '9787020070589', '0', '0', NULL, '《鲁滨孙飘流记》（1719）是笛福的代表作，描写了商人鲁滨孙幸遭遇海难，流落荒岛，以百折不挠的毅力改善生存环境的经历。小说颂扬了劳动精神以及人与自然斗争的坚强意志，主人公鲁滨孙也因此成为世界文学史上一个著名的文学形象。', NULL, 1, 0, NULL, '2015-8-24 00:00:00', NULL, '2016-3-30 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (31, '哈利波特与阿兹卡班囚徒', 1, 19, 4, 'J.K.罗琳', 'img', 10, '人民文学出版社', 300000, 96.00, 248, 27.00, '9787020073474', '0', '0', NULL, '哈利·波特在霍格沃茨魔法学校已经度过了不平凡的两年，而且早已听说魔法世界中有一座守备森严的阿兹卡班监狱，里面关押着一个臭名昭著的囚徒，名字叫小天狼星布莱克。传言布莱克是“黑魔法”高手伏地魔——杀害哈利父母的凶手——的忠实信徒，曾经用一句魔咒接连结束了十三条性命。不幸的是，布莱克逃出了阿兹卡班，一心追寻哈利。布莱克在睡梦中仍然呓语不休：“他在霍格沃茨……他在霍格沃茨。”\n　　哈利·波特虽然身在魔法学校的城堡内，既有朋友的赤诚帮助，也有老师的悉心呵护，但校园内危机四伏，哈利的生命时时受到威胁。一天，布莱克终于站到了哈利的面前……', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2016-3-5 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (32, '影响孩子们一生的经典(插图本)-金银岛', 1, 23, 5, '[英]斯蒂文森/著', 'img', 9, '人民文学出版社', 172000, 71.00, 226, 23.00, '9787020080700', '0', '0', NULL, '《金银岛》是斯蒂文森所有作品中流传最广的代表作，其故事情节起源于作者所画的一幅地图。《金银岛', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (33, '哈利波特与凤凰社', 1, 19, 5, 'J.K.罗琳', 'img', 10, '人民文学出版社', 675000, 96.00, 557, 59.00, '9787020083596', '0', '0', NULL, '《哈利·波特与凤凰社》内容介绍：漫长的暑假，哈利·波特被困在女贞路4号，意外地遭到摄魂怪的袭击……逃过一劫的哈利被护送到伦敦一个秘密的地方，在那里他见到了自己的教父小天狼星布莱克、前任黑魔法防御术课教师卢平、疯眼汉穆迪以及他的朋友赫敏和罗恩。他只是知道好布利多与凤凰社成员正在加紧秘密活动，以对抗日益强大的伏地魔。但是，所有的人都不愿向他透露更多的情况……哈利在茫然与愤怒中来到霍格沃茨，然而令他不解的是，邓布利多不愿见他，海格不知去向。更糟糕的是，哈利越来越频繁地在梦中看见一道长长的走廊，每当他快要走进长廊尽头的一扇门时，他都会头痛欲裂地从梦中惊醒，觉得自己身体里蠕动着一条大蛇……魔法部派来了一位专横跋扈的高级调查官，对学校各项事务横加干涉，并最终赶走了邓布利多校长，打伤了麦格教授，整个学校乱转在一片混乱和压抑之中……大蛇的影子在哈利的脑海里越来越清晰，伏地魔走近了哈利……这时，邓布利多告诉他一个天大的秘密……跟随《哈利·波特与凤凰社》一起来体验这段惊心动魄的历程吧！', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2016-7-12 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (34, '聊斋志异(青少版)', 1, 32, 6, '苏尚耀,蒲松龄，陈葆文,林鸿尧', 'img', 8, '人民文学出版社', 215000, 100.00, 268, 23.00, '9787020087549', '0', '0', NULL, '《聊斋志异》多取材于民间传说和野史轶闻，为我们展示了一个多姿多彩的鬼狐花妖的世界，蒲松龄在《聊斋自志》中说：“集腋为裘，妄续幽冥之录：浮白载笔，仅成孤愤之书。”所以说，它既是一部“搜抉奇怪”、“ 事涉荒幻”的文言短篇小说集，又是作者“触时感事”、“以劝以惩”的孤愤之书。“蒲松龄神鬼狐妖画苍生，驰想天外的志怪，是沧海桑田的人生，人神交往，人鬼交替，人妖转换，花妖狐魅异化为芸芸众生，构成聊斋最和谐的美。《聊斋志异》成为集志怪、神话、寓言于一体的小说宝典。”', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (35, '西游记', 1, 19, 6, '吴承恩', 'img', 8, '人民文学出版社', 213000, 94.00, 251, 23.00, '9787020087617', '0', '0', NULL, '吴承恩编写的《西游记》为中国古典小说“四大名著”其中的一本。在中国古典小说中是运用浪漫主义创作最优秀成功的一部，全书共一百回，八十多万字。第一至第七回是写孙悟空的出身、学艺和大闹天庭等事件，八至十二回叙述唐代高僧玄奘为何会去西方天竺(即印度)取经的缘由，十三至一百回则叙述玄奘在西行取经途中，孙悟空、猪八戒与沙悟净如何协助他渡过重重危险困难，最后圆满达成任务的经过。这本《西游记》，将原书一百回浓缩为三十六个单元，依原作情节事件结构，用当代白话精要生动的写出，字数十万字左右，虽是改写本，但保有故事原貌意趣，非常适合青少年阅读。本书由王蕴纯改写，郑淑芬插画。', NULL, 1, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-9-16 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (36, '中国古典小说(青少版)(1415)-济公传(全两册)', 1, 34, 6, '（清）郭小亭', 'img', 9, '人民文学出版社', 410000, 100.00, 503, 42.00, '9787020087709', '0', '0', NULL, '济公是一个不拘泥于小节的疯和尚；但同时，却也是最喜欢助人于危难的理想和尚。他用自己特质的“伸腿瞪眼丸”给人医病，救了不少人；又专跟坏人作对，在社会上做了一番轰轰烈烈的大事业。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (37, '镜花缘(青少版)', 1, 37, 6, '（清）李汝珍　著，洪小如　改写', 'img', 6, '人民文学出版社', 135000, 100.00, 244, 23.00, '9787020088010', '0', '0', NULL, '　《镜花缘》是一部诞生于清朝嘉庆、道光年间的讽刺小说，更精确地说，是融合幻想、历史、讽刺、游记等小说性质于一炉的皇皇巨著。行文滑稽风趣，善于讽喻嘲弄，又融汇运用古代神话中的虚幻国度，及奇异荒诞的故事情节，藉以寄托对政治社会改革的理想，针砭世道人心的颓废及鄙陋的风习陈规，企盼能劝善敦俗。赞扬优美的传统文化。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (38, '今古奇观(青少版)', 1, 34, 6, '(明)抱瓮老人|改编:林海音', 'img', 8, '人民文学出版社', 213000, 100.00, 236, 22.00, '9787020088171', '0', '0', NULL, '《今古奇观》是明代最优秀的白话短篇小说精选集，形式上不同于《 三国演义》、《水浒传》的长篇巨作，不用耗费长时间阅览；在内容方面也摆脱历史演义或者《西游记》之类的神话夸衍、牛鬼蛇神。总体来说， 更着重从日常生活中取材。全书四十篇故事，大都在述说各种世态人情及悲欢离合的奇特境遇。藉由惊奇而不失真的实际人生事件，让读者在惊叹 感慨之余，从中体会生活的经验教训，获取生存的智慧，领悟人生哲理，陶冶美善品格。简言之，本书的价值：一是具有伦常教化的意义，二是常 中出奇的艺术境界。读来不但令人感到贴近生活，趣味洋溢，而且还具有劝俗导愚的功能。 《今古奇观》由抱瓮老人著，著名作家林海音改写，文字明白流畅，故事简洁生动，不失原著风味，具有很高的文学价值。并配有20多幅插图 。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (39, '中国古典小说(青少版)-刺客传奇', 1, 20, 6, '蔡慧如改写', 'img', 8, '人民文学出版社', 200000, 50.00, 176, 17.00, '9787020088249', '0', '0', NULL, '　本书总共集了六个刺客的故事，包括荆轲、豫让、聂政、专诸、曹沫、曹操。每一个故事对于刺客的出身、动机、过程和结果都有精彩的描述。读者除了细读故事情节之外，也可以体会、想象一下刺客的内心世界。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (40, '中国古典小说(青少版)-郑和下西洋', 1, 24, 6, '（明）罗懋登著，苏尚耀改写', 'img', 8, '人民文学出版社', 200000, 100.00, 226, 22.00, '9787020088393', '0', '0', NULL, '关于郑和这位伟大航海家的生平事迹，在明代小说家罗懋登的《西洋记》里写得很多，但由于书中内容对于神魔妖异的叙述过多，故一向被人视作神魔小说，而将该书有关郑和冒险犯难、敦睦邦交、发扬国威等功绩的主题给淹没了，实在很令人惋惜。因此，为了让青少年读者通过阅读小说，得以深切了解明代这位大航海家的勋绩及当时各国的民情、风俗、产物等，特将洋洋大观的《西洋记》，改写成如今精简易读十多万字的《郑和下西洋》，此外更删除了原书过于荒诞的情节，却又无损于通俗小说的趣味性，以飨读者。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (41, '中国神话(青少版)', 1, 32, 3, '赖惠凤改写', 'img', 10, '人民文学出版社', 200000, 95.00, 241, 22.00, '9787020088409', '0', '0', NULL, '《中国神话(青少版)》是流传在古代中国文学中的种种传说。而这些传说的起源，有各种不同的说法。在这些故事中，从天地的开辟、天上神灵的诞生，到人类的出现和各个朝代的古老传说，都有详尽的叙述。同时，在这些神话当中，也包含了许多可歌可泣的故事。《中国神话》除了选录有关开天辟地的神话以外，也包含了一部分神与人的故事，和一些虽然以人为主，却有神灵参与其间的曲折离奇的故事，各位读者阅读起《中国神话》来，一定会感到有趣。', NULL, 1, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-9-12 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (42, '封神传(青少版)', 1, 32, 6, '朱传誉改写', 'img', 6, '人民文学出版社', 131000, 90.00, 241, 22.00, '9787020088423', '0', '0', NULL, '本书一般俗称《封神榜》，又是一部中国神魔小说，约成书于隆庆、万历年间。全书内容以篇幅巨大、幻想之奇特而闻名于世，展现了古人丰富的想象力。以改写版来说，这本《封神传》所呈现的篇幅算是相当充实的，改写者朱传誉先生又是文坛前辈，对于如何撷取原著精华、如何以浅白的文字让现代读者在阅读时毫无困难，都非常有经验。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-9-19 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (43, '福尔摩斯探案全集·少儿版6-四签名', 1, 17, 5, '柯南·道尔', 'img', 5, '人民文学出版社', 61000, 87.00, 124, 18.00, '9787020088812', '0', '0', NULL, '少女梅丽的父亲摩斯坦失踪多年，仅留下一张有四个签名的纸条。福尔摩斯开始调查后不久，摩斯坦生前的好友舒尔托一家又遭遇不幸，命案现场留有同样的纸条。阴谋、背叛、宝藏、谋杀……一切秘密都隐藏在“四签名”中。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (44, '成语故事', 1, 36, 4, '李新武/编', 'img', 4, '人民文学出版社', 46000, 96.00, 139, 11.00, '9787020089925', '0', '0', NULL, '成语作为语言的重要组成部分，比起一般的词语来具有非常明显的长处：言简意赅，结构谨严，凝练含蓄，富于哲理性而又形象鲜明生动，表现力特别强。', NULL, 1, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (45, '吉吉诺在蚂蚁王国', 1, 32, 6, '万巴', 'img', 7, '人民文学出版社', 176000, 0.00, 212, 25.00, '9787020090518', '0', '0', NULL, '《吉吉诺在蚂蚁王国》讲述了一个美丽动人的故事。吉吉诺是个小学生，他淘气、贪玩、不爱学习，羡慕蚂蚁无忧无虑的生活，结果他真的变成了一只小蚂蚁，徜徉在神奇的昆虫世界里。他起初被众蚂蚁推举为将军，带领着蚂蚁们赶走了侵略者，后来又被拥戴为“小白旗皇帝”，登上了至高无上的宝座。因为有人类的大脑，他到处指手画脚，以为什么都懂，什么都会，结果碰了许多钉子，吃了许多苦头。历经一番波折后，吉吉诺终于翻然醒悟，返回到人类的生活中，变成一个爱学习的好孩子。《吉吉诺在蚂蚁王国》真实地描写了蚂蚁、蜜蜂等昆虫从出生到死亡的生活，赞美了蚂蚁们的团队精神及其完美无瑕的组织性和纪律性，展示了它们以勤劳勇敢为特征的本质。作者万巴把主人公惊险曲折的经历与妙趣横生的描写熔于一炉，不仅向青少年读者传播了昆虫知识，还运用纯熟完美的表现手法达到了普及通俗读物和育人的目的。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (46, '捣蛋鬼日记', 1, 15, 5, '万巴', 'img', 9, '人民文学出版社', 175000, 100.00, 255, 22.00, '9787020094370', '0', '0', NULL, '捣蛋鬼是谁？\n他在姐姐的婚礼上把炮仗拴在了姐夫衣服的扣眼上；他在客厅里表演魔术，差点儿射瞎了客人的眼睛；他在家里玩钓鱼，却钓下了一个老人的牙齿；他在火车上拉下了紧急制动闸，仅仅出于好奇；他自己搞了一个动物园，挂在树上的“猴子”竟是邻居家的小孩……\n妈妈说，因为有了他，她成了世界上最不幸的女人。\n他就是捣蛋鬼加尼诺，一个总是因好奇心而闯祸的孩子。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (47, '世界儿童文学新经典-法老的诅咒', 1, 4, 6, '沃尔夫冈霍尔拜恩', 'img', 9, '人民文学出版社', 290000, 80.00, 323, 29.00, '9787020096282', '0', '0', NULL, '《世界儿童文学新经典:法老的诅咒》主要讲述了，凶手离他这么近，那影子就像是一块蛛丝织成的黑色裹尸布，覆盖着阿肯那顿法老的面孔。法老带着莫大的惊惧，几乎是呐喊：“是你？怎么是你？”他不明白，这是他的大臣和朋友啊！叛徒喊道：“我要杀死你，谁也不知道这里发生的事情……”他举起长矛，深深刺入法老的胸膛……法老呻吟着睁开眼睛：“叛徒！”他用垂死的声音说，“你杀死了我，我要诅咒你！但不是用死亡，那将便宜了你。你将活着……永远得不到安宁……直到有一天……一个死人把我所有的武士从他们的沉睡中唤醒……”三千三百年后，星晨又运行到当年的位置，当老的诅咒就要应验，埃及即将陷入巨大的灾难。就在这一年，德国15岁男孩阿顿的周围怪事不断，为了弄清原由，他勇敢地穿越金字塔下的密室、地下暗河、神猫圣殿，进入帝王谷阿肯那顿法老的秘密陵墓，寻找他多次在梦中见过的诡异隧道。在这里，他找到了一种感觉，原来他在这里已经死过！他就是阿肯那顿法老当年预言将实现他的诅咒的那个死人！那么，那个杀死法老的叛徒是谁呢？他还活着呢？法老的诅咒怎样才能在阿顿的手里实现？命运能不能扭转？阿顿做出了令人难以置信的抉择。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (48, '汤姆的午夜花园', 1, 15, 6, '（英）皮尔斯', 'img', 6, '人民文学出版社', 130000, 100.00, 203, 22.00, '9787020096312', '0', '0', NULL, '菲莉帕·皮尔斯所著的《汤姆的午夜花园（世界儿童文学新经典）》讲述一天深夜，楼下的古董大钟 竟敲了十三下，这时，异的事情发生了，汤姆家平日里放着垃圾桶的后院突然变成了一个神秘的夏日花园 。\n     在这个花园里，汤姆没有脚印也没有影子，还能穿墙而过……随后，一切奇怪的事情接连发生：时间 忽前忽后，季节忽然冬季忽然夏季，人也忽大忽小… …《汤姆的午夜花园（世界儿童文学新经典）》于 1958年获得在世界上知名度颇离的卡内基奖。本书已被拍成电影，被公认为是当代儿童文学的缀典之作。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (50, '莎士比亚戏剧故事集', 1, 25, 6, '查尔斯·兰姆 \n玛丽·兰姆', 'img', 8, '人民文学出版社', 210000, 83.00, 320, 22.00, '9787020099399', '0', '0', NULL, '该书目的“文学”部分，重在“促使学生对社会与人生进行全面的观察、细腻的体味、深入的思考，并在丰富的审美体验中润泽文字、涵养心灵”...', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (51, '福尔摩斯探案全集·少儿版5-恐怖谷', 1, 17, 5, '柯南·道尔', 'img', 5, '人民文学出版社', 62000, 50.00, 124, 29.00, '9787020099856', '0', '0', NULL, '蕴藏丰富煤、铁矿的美国吉尔默敦山脉谷地，为何会成为人人谈之色变的“恐怖谷”？而十二年后发生在英国伯尔斯通村的一场凶杀案，又将牵扯出何等惊人的内幕呢？欲揭晓答案，请看侦探小说《恐怖谷》。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (52, '趣味科学馆', 1, 42, 6, '大泽幸子', 'img', 8, '科学出版社', 223000, 100.00, 226, 39.80, '9787030200105', '0', '0', NULL, '本书旨在为人们设计一本打开神秘世界的指南，因此书中通过图文并茂的形式详细讲解了如肥皂泡不易破的原因、如何用吸管制作乐器、糖是固体还是液体等科学小知识。书中所列实验内容广泛，贴近生活，接近身边事物，实验原材料简单，操作简便，安全可靠，趣味性强。这是一本引导人们手脑并用的理想读物，让人们在动手的同时轻松掌握科学知识。做这些实验能打开大家知识的天窗，带领我们在科学的海洋中畅游。在这个过程中大家肯定会发现，实验并不神秘，科学就在我们身边。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (53, '中华经典故事：中华成语故事', 1, 36, 4, '孙智正/著', 'img', 6, '中华书局', 60000, 97.00, 169, 20.00, '9787101079739', '0', '0', NULL, '中华五千年文明，留下了许多脍炙人口的经典故事。女娲造人、刻舟求剑、苏武牧羊、美入计、新亭对泣、割发代首、毛遂自荐……《中华成语故事》由孙智正编著，精选中华故事中的经典篇章，在保留传统故事精髓的基础上，更加贴近当代读者的阅读需求，从而使读者更容易领悟经典故事所传达出的优秀传统文化精神内核。', NULL, 1, 0, NULL, '2015-8-24 00:00:00', NULL, '2016-1-23 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (54, '中华经典故事：宋词故事', 1, 30, 4, '马丽宁，司垚/著', 'img', 7, '中华书局', 80000, 87.00, 168, 20.00, '9787101087697', '0', '0', NULL, '马丽宁和司壵编著的《宋词故事》内容介绍：中华五千年文明，留下了许多脍炙人口的经典故事。女娲造人、刻舟', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (55, '韩非子故事--中华经典故事', 1, 32, 4, '贺鹏举，郑传敏，刘朔岑　编著', 'img', 7, '中华书局', 80000, 100.00, 170, 20.00, '9787101087703', '0', '0', NULL, '　中华五千年文明，留下了许多脍炙人口的经典故事。女娲造人、刻舟求剑、苏武牧羊、美人计、新亭对泣、割发代首、毛遂自荐……这些故事穿越历史、代代相传、历久弥新，它们彰显着中华民族的传统美德，浓缩了许多做人、做事的道理和智慧，同时还是弘扬中华优秀传统文化、揭示纷繁历史变迁的窗口。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (56, '中国华蒙学经典-千家诗', 1, 30, 4, '张立敏', 'img', 9, '中华书局', 150000, 100.00, 268, 36.00, '9787101088366', '0', '0', NULL, '这《千家诗》是明清两朝流传极广、影响深远的蒙学经典格律诗选本。所选诗歌大多是唐宋时期的名家名篇，易学好懂，题材多样：山水田园、赠友送别、思乡怀人、吊古伤今、咏物题画、侍宴应制等，较广泛地反映了当时的社会现实。“中华蒙学经典”系列图书的《千家诗》，采取原文、注音、注释、插图相结合的形式：原文依据权威底本，全部加注规范的汉语拼音，注释细致精准，并配以明清精美诗意画几十幅，精心编写印制而成。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (57, '中华对联故事--中华经典故事', 1, 32, 4, '刘太品编著', 'img', 8, '中华书局', 100000, 0.00, 205, 25.00, '9787101089394', '0', '0', NULL, '？对联故事在给人以愉悦和欢笑的同时，也能给人以启迪和教育，还具有讽刺、戒勉等社会功能。但对联故事毕竟只是传说故事，不可以看作真实的历史事实，如本书卷首的“王羲之写春联”的故事就是完全出于杜撰，因为在王羲之所处的晋代最多只有在桃木板上画神像的桃符习俗，以红纸书写春联的风俗要到明代中早期才得以形成。《中华对联故事》由刘太品编著，本书精选了数十篇精彩有趣的对联故事，大致以故事主角的历史年代排序。故事还附有“博闻馆”及相关插图，以期起到丰富知识、开拓视野的功效。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (58, '中华经典故事：歇后语故事', 1, 2, 3, '宁明朝/著', 'img', 10, '中华书局', 100000, 100.00, 211, 25.00, '9787101089530', '0', '0', NULL, '《中华经典故事：歇后语故事》选取了74则常见的、典型的歇后语，将歇后语背后的故事用生动的语言呈现在读者面前，加上内容丰富的“博闻馆”和具有知识性的插图，为读者打开了一扇了解中华传统文化的窗户。希望读者们在阅读过程中，了解中华传统文化，喜爱中华传统文化，进而保护和传承中华传统文化。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2016-8-10 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (59, '琴棋书画故事--中华经典故事', 1, 34, 3, '杜鹃', 'img', 10, '中华书局', 100000, 100.00, 187, 22.00, '9787101089813', '0', '0', NULL, '中华五千年文明，留下了许多脍炙人口的经典故事。女娲造人、刻舟求剑、苏武牧羊、美入计、新亭对泣、割发代', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (60, '中华民俗故事--中华经典故事', 1, 32, 4, '黄盼盼编著', 'img', 7, '中华书局', 90000, 100.00, 189, 23.00, '9787101089837', '0', '0', NULL, '《中华经典故事：中华民俗故事》以中华民俗中的节日民俗和节气民俗为主题编著故事，以时间作线索，从节日、节气两个侧面展示中华民族绚烂多姿的民俗风情画卷，通过讲故事来传承中华民俗文化，故事情节生动，引人入胜，具有浓郁的生活气息和传奇色彩，同时又具有浓烈的时代气息。“博闻馆”内容丰富，融知识性和趣味性于一体，符合小学中高年级读者阅读心理和阅读习惯。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-9-19 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (61, '衣食住行故事--中华经典故事', 1, 32, 4, '侯宇亮，种方，王帆编著', 'img', 1, '中华书局', 8700, 100.00, 169, 20.00, '9787101091618', '0', '0', NULL, '中华五千年文明，留下了许多脍炙人口的经典故事。女娲造人、刻舟求剑、苏武牧羊、美入计、新亭对泣、割发代首、毛遂自荐……《中华经典故事：衣食住行故事》中华故事中的经典篇章，在保留传统故事精髓的基础上，更加贴近当代读者的阅读需求，从而使读者更容易领悟经典故事所传达出的优秀传统文化精神内核。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (63, '诺贝尔奖获得者与儿童对话', 1, 3, 6, '（德）施蒂克尔', 'img', 4, '生活.读书.新知三联书店', 86000, 0.00, 202, 39.00, '9787108045218', '0', '0', NULL, '《诺贝尔奖获得者与儿童对话》由贝蒂娜·施蒂克尔所著，本书是诺贝尔奖获得者与儿童对话：有时候我的儿子路易斯会突然出其不意地向我提出一个问题，譬如最近就向我提出这样一个问题：“爸爸，你究竟为什么活着？……”具体内容包括：为什么布丁是软的，石头是硬的？；什么是政治？；为什么要有科学家？；为什么有贫穷和富裕？；为什么我不能光吃油炸土豆条？；我们为什么必须上学？；天空为什么是蓝的？；电话是怎么回事？；不久就有两个我吗？；为什么会有战争？；为什么印第安人不知道疼痛？；妈妈和爸爸为什么必须上班？；究竟是谁发明了戏剧？等一些天真的问题，权威的回答。大人和小孩都值得一读。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (64, '有趣的科学-有趣的力学-感受到的力', 1, 9, 5, '哈蒙德', 'img', 9, '科学普及出版社', 150000, 100.00, 95, 29.90, '9787110059692', '0', '0', NULL, '物理不仅仅意味着飞驶的汽车和砸烂了的苹果。事实上，物理与一切事物都有关，从小到难以想象的构成宇宙的微粒再到大得不可思议的宇宙本身。试想一想，当你试图将两块磁铁的北极挤压在一起时，有一股无形的力量会将它们推开。这个力是什么？又为何存在？你或许会认为，科学家已经破解了所有事物的奥秘，但真实的情况是，科学中充满了神奇和疑惑，而这也是本书为何问题满篇的原因。大多数问题有简单的答案，但有些问题还没有答案。有些问题可能会令你惊奇，其余的会让你感到震惊，还有的仅仅是激发你的思考……希望你能乐在其中。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (65, '有趣的科学-有趣的数学-玩转数与形', 1, 9, 5, '鲍尔', 'img', 9, '科学普及出版社', 150000, 100.00, 95, 29.90, '9787110059708', '0', '0', NULL, '数字从何而来？它们是多么神奇、顽皮、充满乐趣。但是，数字如果不使用，也不会有任何益处。这就是《数学魔术师》这本书的全部意义所在。人类使用数字不只是计数，而且还用来测量。现在科学可能很复杂，但在这本书中，作者向你展示的是，数学如何魔法般地使其变得简单明了。希望你能喜欢这本书，并希望它能为你打开通向数学殿堂的一扇门，让你喜欢上数学和科学。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-9-18 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (66, '有趣的科学-有趣的人体-什么组成我', 1, 8, 5, '[英]温斯顿/著', 'img', 9, '科学普及出版社', 150000, 96.00, 94, 29.90, '9787110059715', '0', '0', NULL, '人体的2／3是水，人体有2006块骨头和大约500万根毛发，人有不可思议的思考和想象能力。人体真的很神奇，那么什么组成了人体？为什么有些人会出现过敏反应？为什么有些人是左撇子？指纹有什么重要作用？……读者可以从本书中找到这些问题的答案。通过阅读本书，读者还可以研究自己的基因，测出自己的性格。\r\n你将在阅读本书时发现，研究人类自身是一件多么有趣的事情。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2016-8-10 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (67, '有趣的科学-有趣的化学：这就是元素', 1, 9, 5, '[英]温斯顿/著', 'img', 9, '科学普及出版社', 150000, 100.00, 95, 29.90, '9787110059722', '0', '0', NULL, '我们已经了解一些基本元素，如铁、铝、氢、氧、银、金。这些元素组成了我们周围的一切：电脑、食物和饮料、汽车和公路，以及我们人类自身。然而，什么是元素？它是如何形成的？我们是何时发现它们的？本书为你提供了指导，帮助你学好化学，让你觉得学习化学其实是一件有趣的事。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2016-8-10 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (68, '探索-恐龙世界', 1, 7, 6, '（英）狄克逊，（英）马伦？', 'img', 8, '科学普及', 200000, 96.00, 95, 32.00, '9787110060308', '0', '0', NULL, '2.5亿年前，地球诞生了新的霸主。它们横行于陆地，几乎无所不能，碧海蓝天同样少不了他们的影迹。然而如此强大的生物却在生活了6.65亿年后悄然毁灭。这是什么生物呢？没错，它就是恐龙，对于这个神秘的生物或许你有很多的疑问和不解，这本从英国DK公司引进的精品图书将会通过逼真的图片和细致的文字让你豁然开朗。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-9-18 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (69, '探索-奇妙地球', 1, 6, 4, '[英]特纳/著', 'img', 10, '科学普及出版社', 200000, 83.00, 95, 32.00, '9787110060322', '0', '0', NULL, '我们生活的地球是个丰富多彩，充满奥秘的世界，地球上的水和氧气为我们的生存提供了必需的生存条件，温和的气候和四季的变迁更是让我们的生活更加舒适自在。对于这些我们都已知晓，可是地球还有许多神奇的现象急需我们去探索。地球内部的结构、板块运动、火山、地震滑坡、沙漠侵蚀等等，这些大自然的现象大家明白吗？气候的变化过程，海啸、飓风的形成和预测都是我们需要了解的，知己知彼才能与之和谐相处，这颗孕育出生命的星球正等待着我们去了解它。\r\n', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2016-8-10 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (70, '探索-气象万千', 1, 6, 6, '伍德沃德', 'img', 8, '科学普及出版社', 200000, 85.00, 95, 32.00, '9787110060339', '0', '0', NULL, '气象，简而言之就是大气的状态和现象，我们日常能感受到的刮风、闪电、打雷、下雪等都是。虽然可以亲身感受得到，可是对于“它”我们又有多少了解呢？大气中的氧气让我们的生命鲜活起来，太阳的光芒让动、植物茁壮成长，雨水滋润着大地，浇灌着农田。可是“气象”的脾气可不是一直那么温顺，万千多变是它的本性。龙卷风、飓风、洪水都是“它”勃然大怒的表现，加上人类对于生态环境的破坏使得气象更加变化无常。在这本引自英国DK公司的图书中作者通过真实的图片和详实的文字对以上种种现象给予了解答，相信大家在读过之后一定会惊叹于气象的神奇，同时认识到保护地球的环境是多么的迫切。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (71, '探索-植物王国', 1, 7, 6, '波涅　', 'img', 8, '科学普及出版社', 200000, 93.00, 95, 32.00, '9787110060353', '0', '0', NULL, '植物几乎生长在世界的所有角落，花草树木看是平凡，实则蕴藏着无数的神奇，给你一个走进植物的王国的机会，让你亲身探索来自这个绿色世界的秘密。本书延续了英国DK公司一贯的精良做工，通过逼真的图片和生动的文字将植物生长发育的各个阶段形象的勾勒出来，尤其对于植物的受粉过程、蒸腾作用和光合作用的讲解更是一目了然。本书不仅介绍了常见的植物，更将肉食性植物和附生、', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (72, '探索-古希腊史', 1, 4, 5, '[英]克里斯普/著　', 'img', 10, '科学普及出版社', 200000, 100.00, 96, 32.00, '9787110060377', '0', '0', NULL, '一提到古希腊你会想到些什么？对后世影响深远的伟大文明，还是雅典之神“雅典娜”，亦或是特洛伊木马。没错', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (73, '探索-哺乳动物', 1, 7, 6, '格林　布鲁尼　', 'img', 8, '科学普及出版社', 200000, 83.00, 95, 32.00, '9787110060414', '0', '0', NULL, '翻开这本《哺乳动物》，目录页旁边的图片所表现出来的温馨瞬间就抓住了我的心。人类本身就是哺乳动物，可对于哺乳动物我们又有多少了解呢？你知道哺乳动物对于自然界的重要性吗？哺乳动物的特征又是什么呢？带着求知的心让我们一起走入哺乳动物的世界吧。本书将哺乳动物按照生活环境分为七大类，从动物的发育、降生、成长、捕食各个方面给予了全面介绍，全书延续了英国DK公司图书一贯的优秀品质，搭配的图片栩栩如生，语言精准到位。尤其在讲解哺乳动物的哺乳方式时，不仅让人感叹生命的神奇与母爱的伟大', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-9-22 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (74, '假如恐龙依然存在', 1, 7, 4, '道格尔·狄克逊', 'img', 2, '科学普及出版社', 20000, 33.00, 95, 39.00, '9787110067871', '0', '0', NULL, '在长达两个多世纪的时间里，恐龙带给我们许多迷惑和惊喜，它们与地球上现存的事物如此不同却又如此真实，它们吸引着我们走进并去探索它们的世界。或许我们不会再有机会亲眼目睹这些史前动物。我们唯一能做的就是充分发挥想象力，我们可以从化石中搜寻出蛛丝马迹，并将其与现生动物进行比较。\n想象一下，如果我们能穿越时光回到过去，亲眼目睹这些栩栩如生的动物呢？如果我们能够研究它们，触摸它们，与它们共同漫步呢？与这些巨兽生活在一起该是件多么激动人心和令人惶恐的事情？我们可以制约它们吗？届时，我们的城市、周围的景观会变成什么样子？海洋和天空又会是如何一种景观呢？假如恐龙依然存在，地球的生态会是什么样子？', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (75, '有趣的科学-有趣的进化-从达尔文到DNA', 1, 8, 5, '温斯顿', 'img', 9, '科学普及出版社', 150000, 100.00, 95, 29.90, '9787110068199', '0', '0', NULL, '还讲述了达尔文传奇般的却又充满悲剧色彩的一生：从他受传统保守宗教教育的童年，到年轻时那次环游地球的冒险旅行，以及他与病痛一生的抗争。虽然达尔文扬名天下，他却是一个性格内向的退休医生的儿子，并在英格兰乡村度过了平静的一生。达尔文还是一个恋家的丈夫和父亲，十岁的女儿安妮患上了猩红热后，他精心地照顾了安妮好几个月，最后却不得不眼睁睁地看着她死在自己的怀抱里。在这悲痛的打击下，达尔文对宗教的信仰开始动摇了，他逐渐产生了一种想法：出版一本关于生命真相的著作，这本书足以改变世界', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (76, '体验大自然-放大镜下发现', 1, 9, 3, '[德]安科•库珀，[德]廷斯•柏沙德/文，[德]阿诺•科尔布/图', 'img', 10, '科学普及出版社', 130000, 0.00, 95, 19.80, '9787110068496', '0', '0', NULL, '\"在池塘、湖泊、河流、小溪流、树林、花同和草地里栖息着无数种千奇百怪的动物：有大的，有小的，有长得非', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (77, '体验大自然-大自然的夜空', 1, 6, 3, '奥弗特林', 'img', 2, '科学普及出版社', 13000, 0.00, 95, 19.80, '9787110068502', '0', '0', NULL, '当傍晚太阳落山后，无数恒星和行星就闪耀在夜空中了。有些星星你可以直接用肉眼看到，而其他距离我们难以想象远的星星则需要用望远镜来观察了。另外还有许多神秘的现象出现在天空中，这些也是你能够观察到的。\n在这奉独特的大自然指南手册的指引下，你将全年都能进行引入人胜的夜间发现之旅。你不仅会学到怎样观察恒犟，怎样将恒星与行星区分开，什么时间在夜空中看到什么星座，还能学习认识我们太阳系的行星', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (78, '体验大自然-海滩漫步寻趣', 1, 6, 3, '奥弗特林', 'img', 2, '科学普及出版社', 13000, 0.00, 95, 19.80, '9787110068519', '0', '0', NULL, '在沙滩上、海岸边、岩石旁和浅滩上，有着各种各样的动植物。这里的生活环境变化多样：在潮起潮落间，海水把海洋中的宝贝送到你的跟前。有些你一眼就能叫出名字，有些刚被你发现就迅速躲起来了，还有一些只能在沙滩上找到它们留下的踪迹。\n这本独特的大自然指南手册将是你野外探索的最佳伴侣，带上它开始你的岸边探索之旅吧。你不仅会了解这非比寻常的岸边自然环境，还可以发现这些可爱的小动物们，去观察它们有趣的生活作息!', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (79, '体验大自然-寻找动物踪迹', 1, 7, 3, '奥弗特林', 'img', 2, '科学普及出版社', 13000, 0.00, 95, 19.80, '9787110068526', '0', '0', NULL, '虽然有无数动物生活在树林、花园、草地、田野里，但你可能只见过它们中的极少数成员。许多动物非常“害羞”，总是把自己藏起来，另一些则习惯昼伏夜出。但当狐狸、兔子、鹿等等动物进食、排泄、在它们的活动区域内生活或生小宝宝时，它们总会留下蛛丝马迹。\n这本书正是教你如何分辨动物的不同踪迹，告诉我们哪些动物生活在我们的周围，以及它们每天的活动情况。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (80, '体验大自然-森林发现之旅', 1, 6, 3, '[德]芭尔贝尔•奥弗特林/著，[德]阿诺•科尔布，[德]托马斯•米勒/图', 'img', 8, '科学普及出版社', 130000, 100.00, 95, 19.80, '9787110068533', '0', '0', NULL, '\"即使是在炎热的夏季，森林里的生命也不像看上去那样沉寂。鸟儿们在清晨就开始了混声合唱，邀请你去发现它', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2016-3-17 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (81, '体验大自然-自然界的方位', 1, 6, 3, '戈尔加斯', 'img', 2, '科学普及出版社', 13000, 0.00, 95, 19.80, '9787110068540', '0', '0', NULL, '到大自然中去探索是一件多么令人激动的事情呀!骑自行车到野外郊游、去林地或田野露营、“全副武装”来个夜间出游，大自然中的一切都是那么新奇有趣。可是，野外不像你熟悉的街道、花园和后院，那里可没有清晰的路牌和方向标志。如果你在野外不认路、东南西北也分不清楚的话，这好玩的探索之旅可能就变得危险了。\n这本书正是教你如何在大自然中辨别方位、如何使用地图和指南针、以及怎么充分准备你的\n探索之旅的。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (82, '体验大自然-冬季里的动物', 1, 7, 3, '莎恩', 'img', 5, '科学普及出版社', 28000, 0.00, 95, 19.80, '9787110068557', '0', '0', NULL, '在冬季，地面冻得硬邦邦的，还覆盖着厚厚的冰雪。湖泊和池塘都结了冰。这对于绝大部分的动物来说无疑是一个艰巨的生存挑战。但是作为勇于探索的大自然观察员的你而言，这些寒意和冰雪根本算不了什么。别忘了戴上帽子和围巾，然后到野外去探索吧。再也没有什么比冬季里的大自然更令人神往了。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (83, '体验大自然-风和雨的故事', 1, 6, 3, '奥弗特林', 'img', 2, '科学普及出版社', 13000, 0.00, 95, 19.80, '9787110068564', '0', '0', NULL, '是晴天还是雨天，是刮风还是下雪，是炎热还是寒冷——天气决定了我们的日常生活。但是天气究竟是怎样产生的?为什么会有四季的区别?怎样根据云朵或者是天空的颜色预测天气的状况?暴风雨是如何发生的?气候变化意味着什么?\n这本独特的大自然指南手册，将会为你解答以上的这些问题，以及更多有趣的知识。本书还将向你介绍所有气象观察的时机，还会告诉你一些避免天气影响的小贴士。不久你就能成为小气象专家，你精准的预言会让你的朋友们大吃一惊!', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (84, '有趣的地理探险-我们居住在哪里', 1, 6, 3, '郝', 'img', 1, '科学普及', 6500, 100.00, 31, 12.90, '9787110068571', '0', '0', NULL, '天真活泼的儿童对眼前的缤纷世界充满了好奇，如何让孩子认识我们居住的世界？如何为孩子讲述我们周围的环境？大自然中都有着什么样的生态环境？《有趣的地理探险》让孩子在趣味地图中，认知这个多彩的世界。地图是我们生活中不可缺少的指南针，《有趣的地理探险——我们居住在哪里》带探索你的家乡和你周围的世界！', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (85, '有趣的地理探险-我们周围的环境', 1, 6, 3, '赫', 'img', 1, '科学普及出版社', 6500, 0.00, 31, 12.90, '9787110068595', '0', '0', NULL, '关于我们周围的环境，地图能告诉我们什么？使用苏珊·郝所著的这本《有趣的地理探险——我们周围的环境》，来探索探索你的家乡和你周围的世界吧！\n    研究你周围的环境，从一所学校到整个世界，来探寻绘制地图的秘密吧！', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (86, '有趣的地理探险-我们喜欢大自然', 1, 6, 3, '郝', 'img', 1, '科学普及出版社', 6500, 0.00, 31, 12.90, '9787110068601', '0', '0', NULL, '关于动物和植物以及它们的生活习性，地图能告诉我们什么？使用苏珊·郝所著的这本《有趣的地理探险——我们喜欢大自然》，来探索探索你的家乡和你周围的世界吧！\n    研究你周围的环境，从一个小花园到整个世界，来探寻绘制地图的秘密！', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (87, '体验大自然-发现夜行动物', 1, 7, 3, '奥弗特林', 'img', 6, '科学普及出版社', 30000, 0.00, 95, 19.80, '9787110072790', '0', '0', NULL, '当夜幕降临时，许多动物都回到藏身之处安歇了，但黑夜绝不像看上去那样沉寂：白天蒙头大睡的刺猬、蝙蝠、猫头鹰、夜蛾和许多其他动物，现在都睡醒了。就像是在春天的清晨，鸟儿们在太阳升起之前就醒来了，用动听的歌声唱着早安；夜行动物们也正邀请你去发现它们的世界。在田野、草甸、森林甚至你的住宅周边，都活跃着哪些夜行动物呢?本书将会告诉你答案。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (88, '体验大自然-动物的孩子们', 1, 7, 3, '戈尔加斯', 'img', 2, '科学普及出版社', 13000, 100.00, 95, 19.80, '9787110072806', '0', '0', NULL, '当小猫咪、狼崽、狐狸仔或是小刺猬这些动物孩子们正在玩耍、打闹、觅食、饮水或是睡觉时，要想对它们进行长时间的观察，该怎么办呢?观察的地点是在家里呢，还是在野外?要是能和这些动物孩子们一起感受慢慢长大的过程，真是令人兴奋不已呀!关于本书所描述的动物，你肯定已经听说过了。但是，关于它们是在什么样的环境中长大的，它们都有着什么样的特性，最重要的是：最好是在什么时候去观察这些娇小可爱的动物孩子们——这些问题你也都知道了吗?', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (89, '体验大自然-一起去观察鸟', 1, 7, 3, '奥弗特林，科尔布', 'img', 2, '科学普及出版社', 13000, 0.00, 95, 19.80, '9787110072813', '0', '0', NULL, '你听过啄木鸟啄木的声音吗?你有没有仔细观察过老鹰捕食?你会用自制的鸟粮吸引鸟儿来觅食吗?\n无论是在野外的森林、草甸或小溪边，还是在人类住宅区的花园或闹市里，我们都可以看到许多不同的鸟类。\n这本独特的大自然指南手册，将是你外出寻找各种鸣禽、猛禽和水禽的最佳伴侣。你可以从本书中获得各种非常实用的寻找和观察鸟儿的建议。\n　　在本书中找一找', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (90, '体验大自然-池塘里的故事', 1, 42, 3, '戈尔加斯', 'img', 2, '科学普及出版社', 13000, 0.00, 95, 19.80, '9787110072820', '0', '0', NULL, '你见过河鳟吗?你有没有仔细观察过一只正在捕食的水鼠耳蝠?你知道水潭和池塘的区别在哪里吗?\n溪流和池塘边有许多新奇事物等待你去发现。当你在岸边和水中看到如此众多的动物和植物，一定会大吃一惊的。\n这本独特的大自然指南手册将会告诉你：怎样准备野外旅行的装备，一些罕见的动物都躲藏在哪里，以及水域观察日记的价值所在。\n你可以从书中获得各种有关溪流和池塘的知识，包括怎样自建池塘。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (91, '体验大自然-节肢动物世界', 1, 7, 3, '库铂', 'img', 2, '科学普及出版社', 13000, 0.00, 95, 19.80, '9787110072837', '0', '0', NULL, '你听说过拟态吗?有没有仔细观察过一只捕食的蜘蛛?昆虫和蜘蛛究竟有什么区别呢?\n不论在树上、水边还是泥土中，甚至你的家里，到处都有虫子在爬动。在我们一起走进节肢动物世界的旅程中，你将会认识各种不同的小生物，并了解它们的生活方式。\n这本独特的大自然指南手册将会告诉你：怎样准备野外旅行的装备，小小的爬虫和飞虫是怎样捕食的，为什么不会有昆虫巨怪存在。你可以从书中获得各种有关昆虫和蜘蛛的知识，以及非常实用的饲养和观察建议。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (92, '有趣的科学-有趣的数学2·数学魔术师', 1, 9, 5, '（英）鲍尔', 'img', 9, '科学普及出版社', 150000, 90.00, 87, 29.90, '9787110072899', '0', '0', NULL, '你能想象没有测量，世界会变成什么样子吗？我们使用数字不只是来计数，而且还用来测量。随着人们越来越聪明，古代的数学魔术师不仅仅学会了测算角度、高度和长度，还建造了神话般的城市；不仅仅测算了地球的大小，还测出了月球到地球的距离。数学，只有使用，才能体现它的意义。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (93, '闪电球探长14-追踪到上海', 1, 17, 3, '（德）舍弗勒', 'img', 10, '科学普及出版社', 120000, 100.00, 122, 15.00, '9787110074589', '0', '0', NULL, '闪电球探长追踪一只可疑的淡紫色泰迪熊来到了上海，和上海的老猫警长一起，他们能成功破获欧元假钞制造案吗？在神秘的桔子酒店隐藏这什么关键人物？谁是犯罪分子的内应？在本册书中，闪电球探长将要和小侦探们一起解开这重重谜团，将犯罪分子绳之以法。\n   闪电为什么会有不同的颜色？爱动脑筋的小侦探们知道筷子是利用了什么原理吗？当小侦探们为了协助闪电球探案而绞尽脑汁并成功破案的同时，还可以在书后的“科普知识串串烧”环节中学到很多日常生活中很有用的知识呢！《闪电球探长》是兼具侦探与科普内容的科学探案系列丛书。闪电球探长反映灵敏，思维如闪电般迅速。他到处惩治坏蛋，从纽约到上海，哪里有坏蛋在做案，哪里就有他破案的身影。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (94, '闪电球探长2-橙色的面具', 1, 17, 3, '（德）舍弗勒', 'img', 10, '科学普及出版社', 120000, 100.00, 128, 15.00, '9787110074619', '0', '0', NULL, '有个戴着橙色面具的坏蛋了洗劫了一家银行，闪电球探长很快就抓住了他。但是，这个坏蛋把橙色的面具和抢来的钱藏在哪儿了？易北河遭到了燃油污染，闪电球探长能找到那个污染环境的罪魁祸首吗？谁是桑拿浴室里的小偷儿？在本册书中，闪电球探长将要和小侦探们一起解开这重重谜团，将犯罪分子绳之以法。\n       铁艺品是如何锻造的？怎样才能像贝克汉姆一样踢出漂亮的香蕉球？当小侦探们为了协助闪电球探案而绞尽脑汁并成功破案的同时，还可以在书后的“科普知识串串烧”环节中学到很多日常生活中很有用的知识呢！', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-9-18 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (95, '闪电球探长8-粉红色的河马', 1, 17, 3, '（德）舍弗勒', 'img', 10, '科学普及出版社', 120000, 100.00, 122, 15.00, '9787110074640', '0', '0', NULL, '在本册书中，闪电球探长将要找出暗藏在粉红色的河马肚子中的惊天秘密，他能成功识破骗子的赌局吗？路面结冰的寒冷冬夜发生了什么可怕的事情？歌剧院上演的著名歌剧背后隐藏着什么大阴谋？\n      河马为什么会是粉红色的？变色龙是怎么变色的呢？当小侦探们为了协助闪电球探案而绞尽脑汁并成功破案的同时，还可以在书后的“科普知识串串烧”环节中学到很多日常生活中很有用的知识呢！', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-9-18 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (96, '闪电球探长12-南瓜幽灵和银色喷漆', 1, 17, 3, '（德）舍弗勒', 'img', 10, '科学普及出版社', 120000, 100.00, 125, 15.00, '9787110074688', '0', '0', NULL, '一个恐怖的南瓜幽灵出现在了小男孩的后院中，通过一罐可疑的银色喷漆，闪电球探长能发现什么呢？鳄鱼的眼神中隐藏着什么重要线索？谁是那个神秘的第七个客人？在本册书中，闪电球探长将要和小侦探们一起解开这重重谜团，将犯罪分子绳之以法。\n      鳄鱼为什么会流泪呢？雷雨季节怎样预防雷击呢？当小侦探们为了协助闪电球探案而绞尽脑汁并成功破案的同时，还可以在书后的“科普知识串串烧”环节中学到很多日常生活中很有用的知识呢！', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (97, '闪电球探长11-白玉象', 1, 17, 3, '[德]乌瑟尔？舍弗勒', 'img', 10, '科学普及出版社', 120000, 0.00, 118, 15.00, '9787110074701', '0', '0', NULL, '古董店里最珍贵的宝贝，镇店之宝——白玉象离奇失踪了！但罪犯的盗窃手段再高明也逃不过闪电球探长精明的侦探头脑。光天化日之下竟然发生了银行抢劫案！布谷鸟医生急需闪电球探长的帮助……在本册书中，闪电球探长将要和小侦探们一起解开这重重谜团，将犯罪分子绳之以法。\n　　夜明珠为什么会发光呢？人的指纹为什么会不一样？当小侦探们为了协助闪电球探案而绞尽脑汁并成功破案的同时，还可以在书后的“科普知识串串烧”环节中学到很多日常生活中很有用的知识呢！', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (98, '闪电球探长10-金色的龙', 1, 37, 3, '（德）舍弗勒', 'img', 10, '科学普及出版社', 120000, 0.00, 119, 15.00, '9787110074718', '0', '0', NULL, '闪电球探长追查一条“金色的龙”来到了中国香港，在那里，他要和他的香港同行们一起同走私犯罪团伙斗智斗勇。但他不知道的是，在警察们内部竟然还有一个罪犯的内线……他能顺利破案吗？在本册书中，闪电球探长将要和小侦探们一起解开这重重谜团，将犯罪分子绳之以法。电报是怎样传送的？安检仪又是怎样进行安检工作的？当小侦探们为了协助闪电球探案而绞尽脑汁并成功破案的同时，还可以在书后的“科普知识串串烧”环节中学到很多日常生活中很有用的知识呢！', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (99, '闪电球探长7-奇幻魔术', 1, 17, 3, '[德]乌瑟尔？舍弗勒', 'img', 10, '科学普及出版社', 120000, 0.00, 128, 15.00, '9787110074725', '0', '0', NULL, '欢乐的游乐场里隐藏着让人琢磨不透的奇幻魔术，是谁偷走了闪电球探长的钱包？是骷髅人还是黑色幽灵？还是吸血鬼？在黑夜中行走的黑色幽灵带来了什么秘密？红茶和蜂蜜蛋糕中藏着什么罪恶？在本册书中，闪电球探长将要和小侦探们一起解开这重重谜团，将犯罪分子绳之以法。\n　　黑色的闪电在旋转吗？如果有人突发心脏病晕倒，你应该怎样做？怎样预防近视眼？当小侦探们为了协助闪电球探案而绞尽脑汁并成功破案的同时，还可以在书后的“科普知识串串烧”环节中学到很多日常生活中很有用的知识呢！', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (100, '闪电球探长6-蓝色的房间', 1, 17, 3, '（德）舍弗勒', 'img', 10, '科学普及出版社', 120000, 100.00, 128, 15.00, '9787110074732', '0', '0', NULL, '有一个骑着自行车的飞贼洗劫了本市的银行，闪电球探长很快就锁定了嫌疑人。但是，这个狡猾的银行抢劫犯把抢来的钱藏在哪儿了呢？面对着一间空荡荡的蓝色房间，闪电球能发现什么线索？斯芬克斯给闪电球探长出了什么谜语？谁偷走了稀有的粉红色钻石？在本册书中，闪电球探长将要和小侦探们一起解开这重重谜团，将犯罪分子绳之以法。\n        为什么雷雨过后，空气格外新鲜？你了解公元纪年法的来历吗？你知道钻石是怎么形成的吗？当小侦探们为了协助闪电球探案而绞尽脑汁并成功破案的同时，还可以在书后的“科普知识串串烧”环节中学到很多日常生活中很有用的知识呢！', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (101, '闪电球探长4-绿色的鹦鹉', 1, 17, 3, '（德）舍佛勒', 'img', 10, '科学普及出版社', 120000, 100.00, 128, 15.00, '9787110074756', '0', '0', NULL, '在特快列车上，一个神秘男人带着的一只神奇的绿色鹦鹉引起了大家的围观。真是一只能说会道的鸟儿啊！但是，闪电球探长透过鸟笼子似乎闻到了一丝阴谋的味道。是谁偷走了一个盛满金币的泡菜坛子？汽车修理工和一只微笑的河马又有什么关联？在本册书中，闪电球探长将要和小侦探们一起解开这重重谜团，将犯罪分子绳之以法。\n     在迁徙中鸟类是如何分辨方向的？河马有几颗牙齿？小企鹅和北极熊之间发生了什么？当小侦探们为了协助闪电球探案而绞尽脑汁并成功破案的同时，还可以在书后的“科普知识串串烧”环节中学到很多日常生活中很有用的知识呢！', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-9-18 00:00:00');
INSERT INTO `read_books` (`id`, `name`, `language`, `type`, `level`, `author`, `imgurl`, `score`, `publisher`, `word_count`, `praise_rate`, `page_count`, `price`, `isbn`, `reader_number`, `recommend_number`, `book_size`, `discription`, `create_tch_id`, `state`, `is_delete`, `creator`, `create_time`, `updator`, `update_time`) VALUES (102, '闪电球探长-闪电球探长在威尼斯(超级版)', 1, 17, 3, '（德）舍弗勒', 'img', 10, '科学普及出版社', 158000, 0.00, 159, 20.00, '9787110074787', '0', '0', NULL, '有人偷走了一把名贵的古董小提琴，沿着古老水城威尼斯弯弯曲曲的幽静河道，爱好冰激凌和意大利面的闪电球探长能否找回那把失窃的小提琴？有一位小骑士是闪电球探长的小帮手，他机智勇敢，帮闪电球探长一起破案，还协助闪电球探长成功解救出了被绑架的小公主，他是谁呢？在本册超级版中，闪电球探长将要和小侦探们一起解开这重重谜团，将犯罪分子绳之以法。', NULL, 0, 0, NULL, '2015-8-24 00:00:00', NULL, '2015-8-25 00:00:00');
